import * as React from 'react';
import { StyleSheet } from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';
import { RootTabScreenProps } from '../types';
import {API, graphqlOperation} from 'aws-amplify';
import {onCreateOrder} from '../src/graphql/subscriptions';
import { listOrders } from '../src/graphql/queries';

export default function TabOneScreen({ navigation }) {

  const [orders, setOrders] = React.useState([])
  const [allOrders, setAllOrders] = React.useState([])

  async function subscribe() {

    try {
      const ordersPromise = API.graphql(graphqlOperation(listOrders))
      const response = await ordersPromise
      setAllOrders([...allOrders, response.listOrders.items])
      console.log(response)
    } catch (err) {
      console.log(err)
    }

    API.graphql(graphqlOperation(onCreateOrder)
    ).subscribe({
        next: (orderData) => {
            console.log("orderData: ", orderData.value.data)
            setOrders([...orders, orderData.value.data])
        }
    })
}


  React.useEffect(() => {
      console.log('work')
      subscribe()

  }, [])

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Tab One</Text>
      <Text>What up</Text>
      {allOrders.map(order => {
        <Text></Text>
      })}
      <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" />
      <EditScreenInfo path="/screens/TabOneScreen.tsx" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
});
