import React, {useState, useEffect} from 'react';
import {API, graphqlOperation} from 'aws-amplify';
import {onCreateOrder} from '../graphql/subscriptions';
import { listOrders } from '../graphql/queries';
import { Text } from '../../components/Themed';

const OrderQueue = () => {

    const [orders, setOrders] = useState([])
    const [allOrders, setAllOrders] = useState([])
    const [isLoading, setIsLoading] = useState(true)

    async function subscribe() {

        try {
        const ordersPromise = API.graphql(graphqlOperation(listOrders))
        const response = await ordersPromise
        setAllOrders([...allOrders, response.data.listOrders.items])
        //console.log(response.data.listOrders.items)
        } catch (err) {
        console.log(err)
        }

        try {
            const orderResponse = API.graphql(graphqlOperation(onCreateOrder)
            ).subscribe({
                next: (orderData) => {
                    console.log("orderData: ", orderData.value.data.onCreateOrder.items)
                    console.log(JSON.parse(orderData.value.data.onCreateOrder.items))
                    const orderItems = JSON.parse(orderData.value.data.onCreateOrder.items)
                    setOrders(orders => [ orderItems])
                }
            })


            setIsLoading(false)



            //console.log(orderResponse)

        } catch (err) {
            console.log(err)
        }

}


  useEffect(() => {
      subscribe()

  }, [])

  /*

{!isLoading ? allOrders[0].map(element => {

                <Text style={{height: '10px'}}>{element.completed}</Text>
            }) : null}

  if (!isLoading) {
    console.log(allOrders[0].map(element => {
        console.log(element.id)
      }))
  } */
    console.log(orders)

    return (
        <div>


            {!isLoading ? orders.map(order => {
                order.map(element => {
                    console.log(element)
                })
            }) : null}
        </div>
    )
}

export default OrderQueue
